// src/components/CreateScreen.js
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addSession } from "../store/scheduleSlice";
import {
  Box,
  Button,
  Checkbox,
  FormControlLabel,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
import "../componentcss/CreateScreen.css";
const CreateScreen = () => {
  const [totalDays, setTotalDays] = useState([]);
  const [formData, setFormData] = useState({
    tuitionId: "",
    tutorName: "",
    startTime: "",
    endTime: "",
    automate: false,
    totalDays: [],
  });
  const [selectedDays, setSelectedDays] = useState({});
  const [sessionTimes, setSessionTimes] = useState({
    sun: {},
    mon: {},
    tue: {},
    wed: {},
    thu: {},
    fri: {},
    sat: {},
  });
  const [commonSession, setCommonSession] = useState({
    sessionStartTime: "",
    sessionEndTime: "",
  });
  const [useCommonSession, setUseCommonSession] = useState(false);
  const dispatch = useDispatch();
  const toggleDaySelection = (day) => {
    setSelectedDays((prev) => {
      const newState = { ...prev };
      if (newState[day]) {
        delete newState[day];
        setSessionTimes((prevTimes) => ({ ...prevTimes, [day]: {} }));
      } else {
        newState[day] = true;
      }
      return newState;
    });
  };
  const handleSessionChange = (day, field, value) => {
    setSessionTimes((prevTimes) => ({
      ...prevTimes,
      [day]: { ...prevTimes[day], [field]: value },
    }));
  };
  const handleSave = () => {
    const result = Object.keys(selectedDays).map((day) => ({
      day,
      sessionStartTime: useCommonSession
        ? commonSession.sessionStartTime
        : sessionTimes[day]?.sessionStartTime || "",
      sessionEndTime: useCommonSession
        ? commonSession.sessionEndTime
        : sessionTimes[day]?.sessionEndTime || "",
    }));
    console.log("Saved Data:", result);
    setTotalDays(result);
    setFormData({
      ...formData,
      totalDays: totalDays,
    });
    console.log("totalDays:", totalDays);
  };
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addSession(formData));
    console.log("Saved Data formData:", formData);

    setFormData({
      tuitionId: "",
      tutorName: "",
      startTime: "",
      endTime: "",
      automate: false,
      totalDays: [],
    });
    setSessionTimes({});
    setSelectedDays({});
    setCommonSession({});
    setUseCommonSession(false);
  };
  const handleCheckboxChange = (checked) => {
    setUseCommonSession(checked);
    if (checked) {
      setSelectedDays({ sun: true, mon: true }); // Select both days when common session is enabled
    }
  };

  console.log("formData", formData);
  return (
    <div>
      <Box className="schedule-main">
        <Typography variant="h4" component="h2" gutterBottom>
          Create Schedule
        </Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {/* Tuition ID */}
            <Grid item xs={12} sm={6}>
              <TextField
                label="Tuition ID"
                name="tuitionId"
                value={formData.tuitionId}
                onChange={handleChange}
                fullWidth
                required
              />
            </Grid>

            {/* Tutor Name */}
            <Grid item xs={12} sm={6}>
              <TextField
                label="Tutor Name"
                name="tutorName"
                value={formData.tutorName}
                onChange={handleChange}
                fullWidth
                required
              />
            </Grid>

            {/* Start Time */}
            <Grid item xs={12} sm={6}>
              <TextField
                label="Session Start Time"
                name="startTime"
                type="time"
                value={formData.startTime}
                onChange={handleChange}
                InputLabelProps={{ shrink: true }}
                fullWidth
                required
              />
            </Grid>

            {/* End Time */}
            <Grid item xs={12} sm={6}>
              <TextField
                label="Session End Time"
                name="endTime"
                type="time"
                value={formData.endTime}
                onChange={handleChange}
                InputLabelProps={{ shrink: true }}
                fullWidth
                required
              />
            </Grid>

            {/* Automate Checkbox */}
            <Grid item xs={12}>
              <FormControlLabel
                control={
                  <Checkbox
                    name="automate"
                    checked={formData.automate}
                    onChange={handleChange}
                  />
                }
                label="Automate"
              />
            </Grid>

            {/* Submit Button */}
            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                fullWidth
              >
                Create
              </Button>
            </Grid>
          </Grid>
        </form>
      </Box>
      <div className="days-grid-main">
        <div className="days-grid-elements">
          {["sun", "mon", "tue", "wed", "thu", "fri", "sat"].map((day) => (
            <div
              key={day}
              onClick={() => toggleDaySelection(day)}
              style={{
                width: "50px",
                height: "50px",
                lineHeight: "50px",
                textAlign: "center",
                border: "1px solid black",
                backgroundColor: selectedDays[day] ? "green" : "transparent",
                color: selectedDays[day] ? "white" : "black",
                cursor: "pointer",
              }}
            >
              {day.toUpperCase()}
            </div>
          ))}
        </div>
        <label>
          <input
            type="checkbox"
            checked={useCommonSession}
            onChange={(e) => handleCheckboxChange(e.target.checked)}
          />
          Use Common Session
        </label>

        {useCommonSession ? (
          <div>
            <label>
              Common Start Time:
              <input
                type="time"
                value={commonSession.sessionStartTime}
                onChange={(e) =>
                  setCommonSession((prev) => ({
                    ...prev,
                    sessionStartTime: e.target.value,
                  }))
                }
              />
            </label>
            <label>
              Common End Time:
              <input
                type="time"
                value={commonSession.sessionEndTime}
                onChange={(e) =>
                  setCommonSession((prev) => ({
                    ...prev,
                    sessionEndTime: e.target.value,
                  }))
                }
              />
            </label>
          </div>
        ) : (
          <div>
            {["sun", "mon", "tue", "wed", "thu", "fri", "sat"].map(
              (day) =>
                selectedDays[day] && (
                  <div key={day}>
                    <h4>{day.toUpperCase()} Session Times</h4>
                    <label>
                      Start Time:
                      <input
                        type="time"
                        value={sessionTimes[day]?.sessionStartTime || ""}
                        onChange={(e) =>
                          handleSessionChange(
                            day,
                            "sessionStartTime",
                            e.target.value
                          )
                        }
                      />
                    </label>
                    <label>
                      End Time:
                      <input
                        type="time"
                        value={sessionTimes[day]?.sessionEndTime || ""}
                        onChange={(e) =>
                          handleSessionChange(
                            day,
                            "sessionEndTime",
                            e.target.value
                          )
                        }
                      />
                    </label>
                  </div>
                )
            )}
          </div>
        )}

        <button onClick={handleSave}>Save</button>
      </div>
    </div>
  );
};

export default CreateScreen;
