// src/components/CreateScreen.js
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addSession } from "../store/scheduleSlice";
import {
  Box,
  Button,
  Checkbox,
  FormControlLabel,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
import DaySelect from './'
const CreateScreen = () => {
  const [formData, setFormData] = useState({
    tuitionId: "",
    tutorName: "",
    startTime: "",
    endTime: "",
    automate: false,
  });

  const dispatch = useDispatch();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addSession(formData));
    setFormData({
      tuitionId: "",
      tutorName: "",
      startTime: "",
      endTime: "",
      automate: false,
    });
  };

  return (
    <Box sx={{ p: 3, maxWidth: 600, mx: "auto" }}>
      <Typography variant="h4" component="h2" gutterBottom>
        Create Schedule
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Tuition ID */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Tuition ID"
              name="tuitionId"
              value={formData.tuitionId}
              onChange={handleChange}
              fullWidth
              required
            />
          </Grid>

          {/* Tutor Name */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Tutor Name"
              name="tutorName"
              value={formData.tutorName}
              onChange={handleChange}
              fullWidth
              required
            />
          </Grid>

          {/* Start Time */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Session Start Time"
              name="startTime"
              type="time"
              value={formData.startTime}
              onChange={handleChange}
              InputLabelProps={{ shrink: true }}
              fullWidth
              required
            />
          </Grid>

          {/* End Time */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Session End Time"
              name="endTime"
              type="time"
              value={formData.endTime}
              onChange={handleChange}
              InputLabelProps={{ shrink: true }}
              fullWidth
              required
            />
          </Grid>

          {/* Automate Checkbox */}
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Checkbox
                  name="automate"
                  checked={formData.automate}
                  onChange={handleChange}
                />
              }
              label="Automate"
            />
          </Grid>

          {/* Submit Button */}
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
            >
              Create
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default CreateScreen;
