import React, { useState } from "react";

const TimesheetApp = () => {
  const [selectedDays, setSelectedDays] = useState({});
  const [sessionTimes, setSessionTimes] = useState({ sun: {}, mon: {} });
  const [commonSession, setCommonSession] = useState({
    sessionStartTime: "",
    sessionEndTime: "",
  });
  const [useCommonSession, setUseCommonSession] = useState(false);
  const [totalDays, setTotalDays] = useState(false);

  const toggleDaySelection = (day) => {
    setSelectedDays((prev) => {
      const newState = { ...prev };
      if (newState[day]) {
        delete newState[day];
        setSessionTimes((prevTimes) => ({ ...prevTimes, [day]: {} }));
      } else {
        newState[day] = true;
      }
      return newState;
    });
  };

  const handleSessionChange = (day, field, value) => {
    setSessionTimes((prevTimes) => ({
      ...prevTimes,
      [day]: { ...prevTimes[day], [field]: value },
    }));
  };

  const handleSave = () => {
    const result = Object.keys(selectedDays).map((day) => ({
      day,
      sessionStartTime: useCommonSession
        ? commonSession.sessionStartTime
        : sessionTimes[day]?.sessionStartTime || "",
      sessionEndTime: useCommonSession
        ? commonSession.sessionEndTime
        : sessionTimes[day]?.sessionEndTime || "",
    }));
    console.log("Saved Data:", result);
  };

  return (
    <div style={{ border: "1px solid black" ,textAlign:'center'}}>
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" ,textAlign:'center'}}>
        {["sun", "mon"].map((day) => (
          <div
            key={day}
            onClick={() => toggleDaySelection(day)}
            style={{
              width: "50px",
              height: "50px",
              lineHeight: "50px",
              textAlign: "center",
              border: "1px solid black",
              backgroundColor: selectedDays[day] ? "green" : "transparent",
              color: selectedDays[day] ? "white" : "black",
              cursor: "pointer",
            }}
          >
            {day.toUpperCase()}
          </div>
        ))}
      </div>

      <label>
        <input
          type="checkbox"
          checked={useCommonSession}
          onChange={(e) => setUseCommonSession(e.target.checked)}
        />
        Use Common Session
      </label>

      {useCommonSession ? (
        <div>
          <label>
            Common Start Time:
            <input
              type="time"
              value={commonSession.sessionStartTime}
              onChange={(e) =>
                setCommonSession((prev) => ({
                  ...prev,
                  sessionStartTime: e.target.value,
                }))
              }
            />
          </label>
          <label>
            Common End Time:
            <input
              type="time"
              value={commonSession.sessionEndTime}
              onChange={(e) =>
                setCommonSession((prev) => ({
                  ...prev,
                  sessionEndTime: e.target.value,
                }))
              }
            />
          </label>
        </div>
      ) : (
        <div>
          {["sun", "mon"].map(
            (day) =>
              selectedDays[day] && (
                <div key={day}>
                  <h4>{day.toUpperCase()} Session Times</h4>
                  <label>
                    Start Time:
                    <input
                      type="time"
                      value={sessionTimes[day]?.sessionStartTime || ""}
                      onChange={(e) =>
                        handleSessionChange(
                          day,
                          "sessionStartTime",
                          e.target.value
                        )
                      }
                    />
                  </label>
                  <label>
                    End Time:
                    <input
                      type="time"
                      value={sessionTimes[day]?.sessionEndTime || ""}
                      onChange={(e) =>
                        handleSessionChange(
                          day,
                          "sessionEndTime",
                          e.target.value
                        )
                      }
                    />
                  </label>
                </div>
              )
          )}
        </div>
      )}

      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default TimesheetApp;
