// src/components/CreateScreen.js
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addSession } from "../store/scheduleSlice";

const CreateScreen = () => {
  const [formData, setFormData] = useState({
    tuitionId: "",
    tutorName: "",
    startTime: "",
    endTime: "",
    automate: false,
  });

  const dispatch = useDispatch();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addSession(formData));
    setFormData({
      tuitionId: "",
      tutorName: "",
      startTime: "",
      endTime: "",
      automate: false,
    });
  };

  return (
    <div>
      <h2>Create Schedule</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tuition ID:</label>
          <input
            type="text"
            name="tuitionId"
            value={formData.tuitionId}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Tutor Name:</label>
          <input
            type="text"
            name="tutorName"
            value={formData.tutorName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Session Start Time:</label>
          <input
            type="time"
            name="startTime"
            value={formData.startTime}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Session End Time:</label>
          <input
            type="time"
            name="endTime"
            value={formData.endTime}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Automate:</label>
          <input
            type="checkbox"
            name="automate"
            checked={formData.automate}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateScreen;
